import React, { useState, useEffect, useRef } from 'react';
import '../Style/Home.scss';
import { Link } from 'react-router-dom';

export default () => {
  const navRef = useRef();
  const aboutRef = useRef();
  const workRef = useRef();
  const contactRef = useRef();

  const [ backgroundColor, setBackgroundColor ] = useState('#f7f3f0');
  const [ navPosition, setNavPosition ] = useState(false);
  const [ navAboutActive, setNavAboutActive ] = useState('');
  const [ navWorkActive, setNavWorkActive ] = useState('');
  const [ navContactActive, setNavContactActive ] = useState('');


  useEffect(() => {
    window.addEventListener('scroll', () => {
      handleNavPosition();
      handleNavEffect();
    });
  }, [])

  const handleNavPosition = () => {
    if(window.pageYOffset > navRef.current.offsetTop * 1.8) {
      setNavPosition(false);
    }else {
      setNavPosition(-window.pageYOffset * .5);
    }
  }

  //다른 방법 생각해보기
  const handleNavEffect = () => {
    if(window.pageYOffset > contactRef.current.offsetTop * 0.9){
      setNavContactActive('--active');
      setNavWorkActive('');
      setNavAboutActive('');
    }else if(window.pageYOffset > workRef.current.offsetTop * 0.9){
      setNavContactActive('');
      setNavWorkActive('--active');
      setNavAboutActive('');
    }else if(window.pageYOffset > aboutRef.current.offsetTop){
      setNavContactActive('');
      setNavWorkActive('');
      setNavAboutActive('--active');
    }
  }


  return (
    <div className='background-container' style={{background: `${backgroundColor}`}}>
      <div className='main-container'>
        <header>
          <h1>
            <span>FRONT-END</span>
            <span>DEVELOPER</span>
          </h1>
          <nav ref={navRef} style={{transform: `translateY(${navPosition}px)`}}>
            <ul className='nav-list'>
              <li className={`nav-list__about ${navAboutActive}`}>
                <span>
                  About
                  <span className='underline' />
                </span>
              </li>
              <li className={`nav-list__work ${navWorkActive}`}>
                <span>
                  Work
                  <span className='underline' />
                </span>
              </li>
              <li className={`nav-list__contact ${navContactActive}`}>
                <span>
                  Contact
                  <span className='underline' />
                </span>
              </li>
            </ul>
          </nav>
        </header>
        <div className='contents'>
          <section className='about' ref={aboutRef}>
            <h1>
              <span>JIA</span>
              <span>SONG</span>
            </h1>
            <p className='intro'>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed vitae tortor ultricies, consequat dolor quis, pretium mauris. Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas. Maecenas porttitor mi in lectus vehicula, non viverra sapien pharetra.
              </p>
            <div className='skills'>
              <h3>What I learn</h3>
              <p>
                JavaScript, React <br />
                CSS3, SASS, Styled-Component, Responsive-Design <br />
                HTML5, Adobe Photoshop
              </p>
              <h3>What I want to learn</h3>
              <p>
                Node.js, TypeScript
              </p>
            </div>
          </section>
          <section className='work' ref={workRef}>
            <h2>WORK</h2>
            <ul className='work-list'>
              <li>
                <Link to='/'>
                  <h3 className='work-title'
                  onMouseEnter={() => setBackgroundColor('blue')} 
                  onMouseLeave={() => setBackgroundColor('#f7f3f0')}>
                    <span>REAL-TIME</span>
                    <span>PAINT</span>
                  </h3>
                  <p className='work-tools'>Node.js</p>
                </Link>
              </li>
              <li>
                <Link to='/'>
                  <h3 className='work-title'
                  onMouseEnter={() => setBackgroundColor('gray')} 
                  onMouseLeave={() => setBackgroundColor('#f7f3f0')}>
                    <span>NETFLIX</span>
                    <span>CLONE</span>
                  </h3>
                  <p className='work-tools'>React, Styled-Component, TMBd API</p>
                </Link>
              </li>
              <li>
                <Link to='/'>
                  <h3 className='work-title'
                  onMouseEnter={() => setBackgroundColor('gray')} 
                  onMouseLeave={() => setBackgroundColor('#f7f3f0')}>
                    <span>NOKCHA</span>
                    <span>STORE</span>
                  </h3>
                  <p className='work-tools'>React Hooks, Redux, SASS</p>
                </Link>
              </li>
              <li>
                <Link to='/'>
                  <h3 className='work-title'
                  onMouseEnter={() => setBackgroundColor('gray')} 
                  onMouseLeave={() => setBackgroundColor('#f7f3f0')}>
                    <span>JAVASCRIPT</span>
                    <span>PROJECT</span>
                  </h3>
                  <p className='work-tools'>JavaScript, CSS3, HTML5</p>
                </Link>
              </li>
              <li>
                <Link to='/'>
                  <h3 className='work-title'
                  onMouseEnter={() => setBackgroundColor('gray')} 
                  onMouseLeave={() => setBackgroundColor('#f7f3f0')}>
                    <span>GYEONGBOK</span>
                    <span>PALACE</span>
                  </h3>
                  <p className='work-tools'></p>
                </Link>
              </li>
            </ul>
          </section>
          <section className='contact' ref={contactRef}>
            <h2>CONTACT</h2>
            <ul className='contact-list'>
              <li>email@email.com</li>
              <li>Github</li>
            </ul>
          </section>
          <footer>
            &copy; 2020 All Rights Reserved.
          </footer>
        </div>
      </div>
    </div>
  )
}
