import React, { useState, useEffect, useRef } from "react";
import "../Style/Home.scss";
import { Link } from "react-router-dom";

export default () => {
  const navRef = useRef(),
    aboutRef = useRef(),
    workRef = useRef(),
    contactRef = useRef();

  const [backgroundColor, setBackgroundColor] = useState("#f7f3f0");
  const [navPosition, setNavPosition] = useState(0);
  const [navUnderline, setNavUnderline] = useState({
    about: "",
    work: "",
    contact: "",
  });

  const [renderAni, setRenderAni] = useState("");
  // const [ contactAni, setContactAni ] = useState('');

  // const [ mousePosition, setMousePosition ] = useState({
  //   x: 0, y:0
  // })

  const [workActive, setWorkActive] = useState({
    NetflixClone: "",
    NokchaStore: "",
    JavascriptProject: "",
    Gyeongbok: "",
  });

  //first render animation
  useEffect(() => {
    setTimeout(() => {
      setRenderAni("--active");
    }, 310);
  }, []);

  const handleNav = (() => {
    useEffect(() => {
      const underlineNav = () => {
        let offsetTop = {
          current: window.pageYOffset,
          nav: navRef.current.offsetTop,
          about: aboutRef.current.offsetTop,
          work: workRef.current.offsetTop,
          contact: contactRef.current.offsetTop,
        };

        if (offsetTop.current > offsetTop.contact * 0.8) {
          setNavUnderline({
            about: "",
            work: "",
            contact: "--active",
          });
        } else if (offsetTop.current > offsetTop.work * 0.8) {
          setNavUnderline({
            about: "",
            work: "--active",
            contact: "",
          });
        } else if (offsetTop.current > offsetTop.about) {
          setNavUnderline({
            about: "--active",
            work: "",
            contact: "",
          });
        }
      };

      const changeNavPosition = () => {
        console.log(window.pageYOffset, window.innerHeight);

        if (window.pageYOffset > window.innerHeight) {
          // setNavPosition(3.125 * 0.01 * window.innerWidth);
          setNavPosition(window.pageYOffset * 0.5);
        } else {
          setNavPosition(3.125 * 0.01 * window.innerWidth);
        }
      };

      window.addEventListener("scroll", changeNavPosition);
      window.addEventListener("scroll", underlineNav);

      return () => {
        window.removeEventListener("scroll", changeNavPosition);
        window.removeEventListener("scroll", underlineNav);
      };
    }, []);
  })();

  const hoverWorkList = {
    inNetflixClone: () => {
      setBackgroundColor("#379683");
      setWorkActive({
        NetflixClone: "--active",
      });
    },
    inNokchaStore: () => {
      setBackgroundColor("#7395ae");
      setWorkActive({
        NokchaStore: "--active",
      });
    },
    inJavascriptProject: () => {
      setBackgroundColor("#557a95");
      setWorkActive({
        JavascriptProject: "--active",
      });
    },
    inGyeongbok: () => {
      setBackgroundColor("#b1a296");
      setWorkActive({
        Gyeongbok: "--active",
      });
    },
    mouseleave: () => {
      setBackgroundColor("#f7f3f0");
      setWorkActive({
        NetflixClone: "",
        NokchaStore: "",
        JavascriptProject: "",
        Gyeongbok: "",
      });
    },
  };

  return (
    <div
      className="background-container"
      style={{ background: `${backgroundColor}` }}
    >
      <div className={`container ${renderAni}`}>
        <header className="Header">
          <h1 className="Header__Title">
            <div className="text-frame">
              <span>Front-end</span>
            </div>
            <div className="text-frame">
              <span>Developer</span>
            </div>
          </h1>
          <nav
            className="Header__Nav"
            ref={navRef}
            // style={{ transform: `translateY(${navPosition}px)` }}
            style={{ top: `${navPosition}px` }}
          >
            <ul className="Header__Nav__List">
              <li
                className={`${navUnderline.about} text-frame`}
                onClick={() =>
                  window.scrollTo({
                    left: 0,
                    top: aboutRef.current.offsetTop,
                    behavior: "smooth",
                  })
                }
              >
                <span>
                  About
                  <span className="underline" />
                </span>
              </li>
              <li
                className={`${navUnderline.work} text-frame`}
                onClick={() =>
                  window.scrollTo({
                    left: 0,
                    top: workRef.current.offsetTop,
                    behavior: "smooth",
                  })
                }
              >
                <span>
                  Work
                  <span className="underline" />
                </span>
              </li>
              <li
                className={`${navUnderline.contact} text-frame`}
                onClick={() =>
                  window.scrollTo({
                    left: 0,
                    top: contactRef.current.offsetTop,
                    behavior: "smooth",
                  })
                }
              >
                <span>
                  Contact
                  <span className="underline" />
                </span>
              </li>
            </ul>
          </nav>
        </header>
        <div className="right-container">
          <section className="About" ref={aboutRef}>
            <h1 className="About__Title">
              <div className="text-frame">
                <span>JIA</span>
              </div>
              <div className="text-frame">
                <span>SONG</span>
              </div>
            </h1>
            <p className="About__Intro">
              <span className="text-frame">
                <span>Lorem ipsum dolor sit amet.</span>
              </span>
              <span className="text-frame">
                <span>
                  consectetur adipiscing elit. Sed vitae tortor ultricies,
                </span>
              </span>
              <span className="text-frame">
                <span>
                  consequat dolor quis, pretium mauris. Pellentesque habitant
                  morbi.
                </span>
              </span>
              <span className="text-frame">
                <span>
                  tristique senectus et netus et malesuada fames ac turpis
                  egestas.
                </span>
              </span>
              <span className="text-frame">
                <span>
                  Maecenas porttitor mi in lectus vehicula, non viverra sapien
                  pharetra.
                </span>
              </span>
            </p>
            <div className="About__Skills">
              <h3 className="About__Skills__Title">What I learn</h3>
              <p>
                JavaScript, React <br />
                CSS3, SASS, Styled-Component, Responsive Design <br />
                HTML5, Adobe Photoshop
              </p>
              <h3 className="About__Skills__Title">
                What I want to learn more
              </h3>
              <p>Node.js, TypeScript, GraphQL</p>
            </div>
          </section>
          <section className="Work" ref={workRef}>
            <h2 className="Work__Title">WORK</h2>
            <ul className="Work__List">
              <li className={workActive.NokchaStore}>
                <Link to="/NokchaStore">
                  <h3
                    className="Work__List__Title"
                    onMouseEnter={hoverWorkList.inNokchaStore}
                    onMouseLeave={hoverWorkList.mouseleave}
                  >
                    <div className="text-frame--in_Work">
                      <span>NOKCHA</span>
                      <span className="hidden-text">NOKCHA</span>
                    </div>
                    <div className="text-frame--in_Work">
                      <span>STORE</span>
                      <span className="hidden-text">STORE</span>
                    </div>
                  </h3>
                </Link>
                <p className="Work__List__Tech">
                  React Hooks, Redux, TypeScript, SASS, GraphQL
                </p>
                <div className="Work__List__Img">
                  <img src={require("../Assets/nokchaStore.png")} alt="d" />
                </div>
                <div className="circle">
                  <div className="arrow">
                    <span>&uarr;</span>
                    <span className="hidden-arrow">&uarr;</span>
                  </div>
                </div>
              </li>
              <li className={workActive.NetflixClone}>
                <Link to="/NetflixClone">
                  <h3
                    className="Work__List__Title"
                    onMouseEnter={hoverWorkList.inNetflixClone}
                    onMouseLeave={hoverWorkList.mouseleave}
                  >
                    <div className="text-frame--in_Work">
                      <span>NETFLIX</span>
                      <span className="hidden-text">NETFLIX</span>
                    </div>
                    <div className="text-frame--in_Work">
                      <span>CLONE</span>
                      <span className="hidden-text">CLONE</span>
                    </div>
                  </h3>
                </Link>
                <p className="Work__List__Tech">
                  React Hooks, Redux, Styled-Component, TMBd API
                </p>
                <div className="Work__List__Img">
                  <img src={require("../Assets/netflixClone.png")} alt="d" />
                </div>
                <div className="circle">
                  <div className="arrow">
                    <span>&uarr;</span>
                    <span className="hidden-arrow">&uarr;</span>
                  </div>
                </div>
              </li>
              <li className={workActive.JavascriptProject}>
                <Link to="/JavascriptProject">
                  <h3
                    className="Work__List__Title"
                    onMouseEnter={hoverWorkList.inJavascriptProject}
                    onMouseLeave={hoverWorkList.mouseleave}
                  >
                    <div className="text-frame--in_Work">
                      <span>JAVASCRIPT</span>
                      <span className="hidden-text">JAVASCRIPT</span>
                    </div>
                    <div className="text-frame--in_Work">
                      <span>PROJECT</span>
                      <span className="hidden-text">PROJECT</span>
                    </div>
                  </h3>
                </Link>
                <p className="Work__List__Tech">
                  ES6, MVC Pattern, CSS3, HTML5
                </p>
                <div className="Work__List__Img">
                  <img
                    src={require("../Assets/javascriptProject.png")}
                    alt="d"
                  />
                </div>
                <div className="circle">
                  <div className="arrow">
                    <span>&uarr;</span>
                    <span className="hidden-arrow">&uarr;</span>
                  </div>
                </div>
              </li>
              <li className={workActive.Gyeongbok}>
                <Link to="/Gyeongbok">
                  <h3
                    className="Work__List__Title"
                    onMouseEnter={hoverWorkList.inGyeongbok}
                    onMouseLeave={hoverWorkList.mouseleave}
                  >
                    <div className="text-frame--in_Work">
                      <span>GYEONGBOK</span>
                      <span className="hidden-text">GYEONGBOK</span>
                    </div>
                    <div className="text-frame--in_Work">
                      <span>PALACE</span>
                      <span className="hidden-text">PALACE</span>
                    </div>
                  </h3>
                </Link>
                <p className="Work__List__Tech">HTML5, CSS3, JavaScript</p>
                <div className="Work__List__Img">
                  <img src={require("../Assets/netflixClone.png")} alt="d" />
                </div>
                <div className="circle">
                  <div className="arrow">
                    <span>&uarr;</span>
                    <span className="hidden-arrow">&uarr;</span>
                  </div>
                </div>
              </li>
            </ul>
          </section>
          <section className="Contact" ref={contactRef}>
            <h2 className="Contact__Title">CONTACT</h2>
            <ul className="Contact__List">
              <li>
                <a href="mailto: jiasong214@gmail.com">jiasong214@gmail.com</a>
              </li>
              <li>
                <a href="https://github.com/JiaSong214" target="_blank">
                  Github
                </a>
              </li>
            </ul>
          </section>
          <footer>&copy; 2020 All Rights Reserved.</footer>
        </div>
      </div>
    </div>
  );
};
