import React, { useEffect, useState } from 'react';
import { Route, Redirect, Switch, withRouter } from "react-router-dom";
import { CSSTransition, TransitionGroup } from 'react-transition-group';

import Home from '../Routes/Home';
import NetflixClone from '../Routes/NetflixClone';
import NokchaStore from '../Routes/NokchaStore';
import JavascriptProject from '../Routes/JavascriptProject';

const RoutesPath = ({ location }) => {
  const [ backgroundColor, setBackgroundColor ] = useState('#f7f3f0');

  useEffect(() => {
    if(location.pathname !== '/') {
      setTimeout(() => {
        window.scrollTo(0,0);
      },100)

      if(location.pathname === '/NetflixClone') {
        setBackgroundColor('green')
      }else if(location.pathname === '/NokchaStore') {
        setBackgroundColor('blue')
      }else if(location.pathname === '/JavascriptProject') {
        setBackgroundColor('red')
      }
    }else if(location.pathname === '/') {
      setBackgroundColor('#f7f3f0')
    }
  });

  return (
    <TransitionGroup>
      <CSSTransition key={location.pathname} timeout={1000}>
        <div className='transition-container' style={{background: `${backgroundColor}`}}>
          <Switch location={location}>
            <Route exact path='/' component={Home} />
            <Route path='/NetflixClone' component={NetflixClone} />
            <Route path='/NokchaStore' component={NokchaStore} />
            <Route path='/JavascriptProject' component={JavascriptProject} />
            <Route component={() => <Redirect to='/' />} />
          </Switch>
        </div>
      </CSSTransition>
    </TransitionGroup>
  );
}

export default withRouter(RoutesPath);
