import React, { useState, useEffect, useRef } from 'react';
import '../Style/Home.scss';
import { Link } from 'react-router-dom';

export default () => {
  const navRef = useRef();
  const aboutRef = useRef();
  const workRef = useRef();
  const contactRef = useRef();

  const [ backgroundColor, setBackgroundColor ] = useState('#f7f3f0');
  const [ navPosition, setNavPosition ] = useState(false);
  const [ navAboutActive, setNavAboutActive ] = useState('');
  const [ navWorkActive, setNavWorkActive ] = useState('');
  const [ navContactActive, setNavContactActive ] = useState('');

  const [ aboutAni, setAboutAni ] = useState('');
  // const [ workAni, setWorkAni ] = useState('');
  // const [ contactAni, setContactAni ] = useState('');

  const [ currentOffsetTop, setCurrentOffsetTop ] = useState(0);
  const [ navOffsetTop, setNavOffsetTop ] = useState(0);

  const [ offsetTop, setOffsetTop ] = useState({
    current: 0,
    nav: 0,
    about: 0,
    work: 0,
    contact: 0
  })


  useEffect(() => {
    setTimeout(() => {
      setAboutAni('--active');
    }, 10);

    // setOffsetTop({
    //   current: window.pageYOffset,
    //   nav: navRef.current.offsetTop,
    //   about: aboutRef.current.offsetTop,
    //   work: workRef.current.offsetTop,
    //   contact: contactRef.current.offsetTop
    // })

    // const handleNavPosition = () => {      
    //   if(offsetTop.current > offsetTop.nav * 1.9) {
    //     setNavPosition(false);
    //   }else {
    //     setNavPosition(-offsetTop.current * .5);
    //   }
    // }

    console.log(offsetTop)

    // const handleNavEffect = () => {
    //   if(currentOffsetTop > contactOffsetTop * 0.9){
    //     setNavContactActive('--active');
    //     setNavWorkActive('');
    //     setNavAboutActive('');
    //   }else if(currentOffsetTop > workOffsetTop * 0.9){
    //     setNavContactActive('');
    //     setNavWorkActive('--active');
    //     setNavAboutActive('');
    //   }else if(currentOffsetTop > aboutOffsetTop){
    //     setNavContactActive('');
    //     setNavWorkActive('');
    //     setNavAboutActive('--active');
    //   }
    // }

    window.addEventListener('scroll', () => {
      setOffsetTop({
        current: window.pageYOffset,
        nav: navRef.current.offsetTop,
        about: aboutRef.current.offsetTop,
        work: workRef.current.offsetTop,
        contact: contactRef.current.offsetTop
      })

      handleNavPosition();
      // handleNavEffect();
    });

    // console.log('useEffect');
  }, []);

  // console.log('outside of useEffect')

    const handleNavPosition = () => {      
      if(offsetTop.current > offsetTop.nav * 1.9) {
        setNavPosition(false);
      }else {
        setNavPosition(-offsetTop.current * .5);
      }
    }



  return (
    <div className='background-container' style={{background: `${backgroundColor}`}}>
      <div className='main-container' >
        <header className={`${aboutAni}`}>
          <h1 className='portfolio-title'>
            <div className='text-animation-container'>
              <div className='text-frame'>
                <span>Front-end</span>
              </div>
              <div className='text-frame'>
                <span>Developer</span>
              </div>
            </div>
          </h1>
          <nav ref={navRef} style={{transform: `translateY(${navPosition}px)`}}>
            <ul className='nav-list text-animation-container'>
              <li className={`nav-list__about ${navAboutActive} text-frame`}>
                <span>
                  About
                  <span className='underline' />
                </span>
              </li>
              <li className={`nav-list__work ${navWorkActive} text-frame`}>
                <span>
                  Work
                  <span className='underline' />
                </span>
              </li>
              <li className={`nav-list__contact ${navContactActive} text-frame`}>
                <span>
                  Contact
                  <span className='underline' />
                </span>
              </li>
            </ul>
          </nav>
        </header>
        <div className='contents'>
          <section className={`about ${aboutAni}`} ref={aboutRef}>
            <div className='viewpoint'>
              <div className='viewpoint-innerbox'>
                <h1 className='section-title'>
                <div className='text-animation-container'>
                  <div className='text-frame'>
                    <span>JIA</span>
                  </div>
                  <div className='text-frame'>
                    <span>SONG</span>
                  </div>
                </div>
              </h1>
                <p className='intro text-animation-container'>
                  <span className='text-frame'>
                    <span>Lorem ipsum dolor sit amet.</span>
                  </span>
                  <span className='text-frame'>
                    <span>consectetur adipiscing elit. Sed vitae tortor ultricies,</span>
                  </span>
                  <span className='text-frame'>
                    <span>consequat dolor quis, pretium mauris. Pellentesque habitant morbi.</span>
                  </span>
                  <span className='text-frame'>
                    <span>tristique senectus et netus et malesuada fames ac turpis egestas.</span>
                  </span>
                  <span className='text-frame'>
                    <span>Maecenas porttitor mi in lectus vehicula, non viverra sapien pharetra.</span>
                  </span>
              </p>
              </div>
            </div>
            <div className='skills'>
              <h3>What I learn</h3>
              <p>
                JavaScript, React <br />
                CSS3, SASS, Styled-Component, Responsive-Design <br />
                HTML5, Adobe Photoshop
              </p>
              <h3>What I want to learn</h3>
              <p>
                Node.js, TypeScript
              </p>
            </div>
          </section>
          <section className='work' ref={workRef}>
            <h2 className='section-title'>WORK</h2>
            <ul className='work-list'>
              <li onMouseEnter={() => setBackgroundColor('#5d5c61')} 
                  onMouseLeave={() => setBackgroundColor('#f7f3f0')}>
                <Link to='/'>
                  <h3 className='work-title'>
                    <div className='text-frame'>
                      <span>REAL-TIME</span>
                      <span className='hidden-text'>REAL-TIME</span>
                    </div>
                    <div className='text-frame'>
                      <span>PAINT</span>
                      <span className='hidden-text'>PAINT</span>
                    </div>
                  </h3>
                  <p className='work-tools'>Node.js</p>
                  <div className='circle'>
                    <div className='arrow'>
                      <span>&uarr;</span>
                      <span className='hidden-arrow'>&uarr;</span>
                    </div>
                  </div>
                </Link>
              </li>
              <li onMouseEnter={() => setBackgroundColor('#379683')} 
                  onMouseLeave={() => setBackgroundColor('#f7f3f0')}>
                <Link to='/NetflixClone'>
                  <h3 className='work-title'>
                    <div className='text-frame'>
                      <span>NETFLIX</span>
                      <span className='hidden-text'>NETFLIX</span>
                    </div>
                    <div className='text-frame'>
                      <span>CLONE</span>
                      <span className='hidden-text'>CLONE</span>
                    </div>
                  </h3>
                  <p className='work-tools'>React, Styled-Component, TMBd API</p>
                  <div className='circle'>
                    <div className='arrow'>
                      <span>&uarr;</span>
                      <span className='hidden-arrow'>&uarr;</span>
                    </div>
                  </div>
                </Link>
              </li>
              <li onMouseEnter={() => setBackgroundColor('#7395ae')} 
                  onMouseLeave={() => setBackgroundColor('#f7f3f0')}>
                <Link to='/NokchaStore'>
                  <h3 className='work-title'>
                    <div className='text-frame'>
                      <span>NOKCHA</span>
                      <span className='hidden-text'>NOKCHA</span>
                    </div>
                    <div className='text-frame'>
                      <span>STORE</span>
                      <span className='hidden-text'>STORE</span>
                    </div>
                  </h3>
                  <p className='work-tools'>React Hooks, Redux, SASS</p>
                  <div className='circle'>
                    <div className='arrow'>
                      <span>&uarr;</span>
                      <span className='hidden-arrow'>&uarr;</span>
                    </div>
                  </div>
                </Link>
              </li>
              <li onMouseEnter={() => setBackgroundColor('#557a95')} 
                  onMouseLeave={() => setBackgroundColor('#f7f3f0')}>
                <Link to='/JavascriptProject'>
                  <h3 className='work-title'>
                    <div className='text-frame'>
                      <span>JAVASCRIPT</span>
                      <span className='hidden-text'>JAVASCRIPT</span>
                    </div>
                    <div className='text-frame'>
                      <span>PROJECT</span>
                      <span className='hidden-text'>PROJECT</span>
                    </div>
                  </h3>
                  <p className='work-tools'>JavaScript, CSS3, HTML5</p>
                  <div className='circle'>
                    <div className='arrow'>
                      <span>&uarr;</span>
                      <span className='hidden-arrow'>&uarr;</span>
                    </div>
                  </div>
                </Link>
              </li>
              <li onMouseEnter={() => setBackgroundColor('#b1a296')} 
                  onMouseLeave={() => setBackgroundColor('#f7f3f0')}>
                <Link to='/'>
                  <h3 className='work-title'>
                    <div className='text-frame'>
                      <span>GYEONGBOK</span>
                      <span className='hidden-text'>GYEONGBOK</span>
                    </div>
                    <div className='text-frame'>
                      <span>PALACE</span>
                      <span className='hidden-text'>PALACE</span>
                    </div>
                  </h3>
                  <p className='work-tools'></p>
                  <div className='circle'>
                    <div className='arrow'>
                      <span>&uarr;</span>
                      <span className='hidden-arrow'>&uarr;</span>
                    </div>
                  </div>
                </Link>
              </li>
            </ul>
          </section>
          <section className='contact' ref={contactRef}>
            <h2 className='section-title'>CONTACT</h2>
            <ul className='contact-list'>
              <li>email@email.com</li>
              <li>Github</li>
            </ul>
          </section>
          <footer>
            &copy; 2020 All Rights Reserved.
          </footer>
        </div>
      </div>
    </div>
  )
}
